export default {
  _b64ToUtf8(b64) {
    try {
      const clean = (b64 || "").replace(/^data:.*;base64,/, "");
      const binStr = atob(clean);
      const bytes = Uint8Array.from(binStr, c => c.charCodeAt(0));
      const decoder = new TextDecoder("utf-8", { fatal: false });
      return decoder.decode(bytes);
    } catch (e) {
      return "";
    }
  },

  _basenameNoExt(name) {
    if (!name) return "untitled";
    const base = name.split(/[\\/]/).pop();
    return base.replace(/\.[^.]+$/, "");
  },

  async importFiles() {
    const files = fp_iso_files.files || [];
    if (!files.length) {
      showAlert("Select at least one .txt file.", "warning");
      return;
    }

    const tags = i_import_tags.text || null;
    let imported = 0;

    for (const f of files) {
      const title = this._basenameNoExt(f.name);
      const full_text = this._b64ToUtf8(f.data);

      if (!full_text || !full_text.trim()) {
        showAlert(`File ${f.name} is empty or unreadable.`, "warning");
        continue;
      }

      await ins_iso_text_one.run({
        title,
        tags,
        full_text
      });

      imported++;
    }

    closeModal("j_iso_import_modal");
    ISOTags.refresh();
    ISOTextSearch.search();

    showAlert(`Imported ${imported} text file(s).`, "success");
  }
};
