onClick:
{{
(async () => {
  const id = Number(appsmith.store.caccRow?.customer_account_id);
  if (!id) { showAlert('Missing account id.', 'error'); return; }

  try {
    await upd_cacc_soft_del.run({ customer_account_id: id });
    await get_cacc_list.run();

    if (typeof tbl_cacc.clearSelection === 'function') { tbl_cacc.clearSelection(); }
    await storeValue('caccRow', null);
    closeModal('j_cacc_del_m');
    showAlert('Account archived.', 'success');
  } catch (e) {
    showAlert('Delete failed: ' + (e?.message || e), 'error');
  }
})()
}}
