onClick:
{{
  (async () => {
    if (appsmith.store.pageBusy) return;
    await storeValue('pageBusy', true);
    try {
      // Main invoices
      await get_invoices.run();

      // Side invoice preview (if any invoice is selected for preview)
      if (appsmith.store.invPrevId) {
        await get_inv_proj_prev.run();
      }

      // Update modal context (if an invoice is open/selected)
      if (appsmith.store.invId) {
        await get_inv_by_id.run().catch(() => {});
        await get_inv_projects.run().catch(() => {});
        await get_uninv_proj_by_inv.run().catch(() => {});
      }

      // Global "Not Invoiced Projects" preview
      await get_uninv_proj_all.run();

      showAlert('Page refreshed.', 'success');
    } catch (e) {
      showAlert('Refresh error: ' + (e?.message || e), 'error');
    } finally {
      await storeValue('pageBusy', false);
    }
  })()
}}
