onClick:
{{
  (async () => {
    if (!p_uninv_proj_table.selectedRows.length) {
      showAlert('Select at least one project', 'warning'); return;
    }
    try {
      await ins_inv_links_json.run();         // link projects
      await upd_proj_set_invoiced_json.run(); // set Invoiced

      // Refresh Update modal table (linked)
      await get_inv_projects.run();

      // Refresh "uninv by invoice" list (if modal stays open later)
      await get_uninv_proj_by_inv.run();

      // Refresh global "all not invoiced" side preview
      await get_uninv_proj_all.run();

      // Refresh side invoice preview if it shows this same invoice
      if (appsmith.store.invPrevId === appsmith.store.invId) {
        await get_inv_proj_prev.run();
      }

      closeModal('j_inv_add_proj_m');
      showModal('j_inv_upd_m');
      showAlert('Projects added to invoice.', 'success');
    } catch (e) {
      showAlert('Add projects error: ' + (e?.message || e), 'error');
    }
  })()
}}
