onClick:
{{
  (async () => {
    // ---- required fields
    if (!i_cust_id.selectedOptionValue) { showAlert('Choose a customer','warning'); return; }
    if (!i_curr_id.selectedOptionValue) { showAlert('Choose a currency','warning'); return; }
    if (!i_inv_date.selectedDate)       { showAlert('Choose invoice date','warning'); return; }
    if (!i_due_date.selectedDate)       { showAlert('Choose due date','warning'); return; }

    try {
      // 1) insert header (reuses your query)
      await ins_invoice.run();

      // 2) resolve new invoice id (no new queries)
      let invId = null;

      // 2a) try your helper first
      try {
        const rs  = await get_last_inv_id.run();
        const dat = Array.isArray(rs) ? rs : get_last_inv_id.data;
        invId = dat?.[0]?.customer_invoice_id ?? dat?.[0]?.last_id ?? null;
      } catch (_) {}

      // 2b) fallback: refresh main table and pick the max id visible
      if (!invId) {
        await get_invoices.run();
        const rows = get_invoices.data || [];
        invId = rows.reduce((m, r) => Math.max(m, Number(r.customer_invoice_id || 0)), 0) || null;
      }

      if (!invId) { showAlert('Could not resolve new invoice id','error'); return; }

      // 3) load canonical row for Update modal
      await storeValue('invId', invId);
      await get_inv_by_id.run();
      const inv = get_inv_by_id.data?.[0] || {};
      await storeValue('invRow', inv);

      // 4) close New, open Update, prefill (Customer/Currency read-only there)
      closeModal('j_inv_ins_m');
      showModal('j_inv_upd_m');

      u_cust_id.setSelectedOption({
        label: inv.customer_name || i_cust_id.selectedOptionLabel || '',
        value: inv.customer_id   || i_cust_id.selectedOptionValue
      });
      u_currency.setSelectedOption({
        label: inv.currency_acronym || i_curr_id.selectedOptionLabel || '',
        value: inv.currency_id      || i_curr_id.selectedOptionValue
      });

      // dates (prefer DB row; fall back to what user picked)
      u_inv_date.setValue(
        inv.customer_invoice_date
          ? moment(inv.customer_invoice_date).format('YYYY-MM-DD')
          : moment(i_inv_date.selectedDate).format('YYYY-MM-DD')
      );
      u_due_date.setValue(
        inv.customer_invoice_due_date
          ? moment(inv.customer_invoice_due_date).format('YYYY-MM-DD')
          : moment(i_due_date.selectedDate).format('YYYY-MM-DD')
      );

      // 5) refresh Update-modal tables
      await get_inv_projects.run();
      await get_uninv_proj_by_inv.run();

      showAlert('Invoice created','success');
    } catch (e) {
      showAlert('Save failed: ' + (e?.message || e), 'error');
    }
  })()
}}
