onClick:
{{
  (function () {
    const key = 'pli_del';
    if (!SubmitShield.lock(key)) return;

    del_pli.run(
      async () => {
        await get_pli_list.run();     // refresh table
        SubmitShield.release(key);
        closeModal('j_pli_del_m');
        showAlert('Item deleted.', 'success');
      },
      (err) => {
        SubmitShield.release(key);
        closeModal('j_pli_del_m');
        showAlert('Delete failed: ' + (err?.message || JSON.stringify(err)), 'error');
      }
    );
  })()
}}
