onClick:
{{
(async () => {
  const curId = dd_bm_cur.selectedOptionValue;
  if (!curId) {
    showAlert("Select a Benchmark currency first.", "warning");
    return;
  }

  let plId = get_pl_hdr_bm.data?.[0]?.price_list_id;

  if (!plId) {
    await ins_pl_hdr_bm.run();
    await get_pl_hdr_bm.run();
    plId = get_pl_hdr_bm.data?.[0]?.price_list_id;
  }

  if (!plId) {
    showAlert("Could not create/find the Benchmark price list.", "error");
    return;
  }

  await storeValue("plCtx", { scope: "BENCHMARK", price_list_id: plId });
  resetWidget("jf_pl_item_i");
  showModal("j_pl_item_i");
})()
}}
