onClick:
{{
(async () => {
  const accId = dd_pl_acc.selectedOptionValue;
  if (!accId) {
    showAlert("Select a Customer Account first.", "warning");
    return;
  }

  let plId = get_pl_hdr_acc.data?.[0]?.price_list_id;

  if (!plId) {
    await ins_pl_hdr_acc.run();
    await get_pl_hdr_acc.run();
    plId = get_pl_hdr_acc.data?.[0]?.price_list_id;
  }

  if (!plId) {
    showAlert("Could not create/find the Account price list.", "error");
    return;
  }

  await storeValue("plCtx", { scope: "ACCOUNT", price_list_id: plId });
  resetWidget("jf_pl_item_i");
  showModal("j_pl_item_i");
})()
}}
