onClick:
{{
(async () => {
  if (appsmith.store.po_del_busy) return;
  await storeValue('po_del_busy', true);

  const po = appsmith.store.poDelRow;
  if (!po?.purchase_order_id) {
    showAlert('Missing PO id', 'error');
    await storeValue('po_del_busy', false);
    return;
  }

  try {
    await del_po_links.run();                   // remove junction links first
    await del_po.run();                         // delete the PO
    await get_purchase_orders_update.run();     // refresh PO table inside j_proj_upd_m
    await get_project_po_total.run();           // refresh totals
    await get_po_preview.run();                 // ðŸ”„ refresh read-only preview under Projects_Table
    closeModal('j_po_del_m');
    await storeValue('poDelRow', null);
    showAlert('Purchase order deleted', 'success');
  } catch (e) {
    showAlert('Delete failed: ' + (e?.message || e), 'error');
  } finally {
    await storeValue('po_del_busy', false);
  }
})()
}}
