onClick:
{{
  SubmitShield.guard() &&
  upd_po.run(
    async () => {
      await get_purchase_orders_update.run();   // refresh PO table inside j_proj_upd_m
      await get_project_po_total.run();         // refresh totals
      await get_po_preview.run();               // ðŸ”„ refresh read-only preview under Projects_Table
      closeModal('j_po_upd_m');
      showModal('j_proj_upd_m');                // stay in project context
      showAlert('Purchase order updated', 'success');
    },
    (e) => showAlert('Update PO failed: ' + (e?.message || e), 'error')
  )
}}
