(async () => {
  const rows = appsmith.store.projBulkRows || [];
  if (!rows.length) { showAlert('Nothing selected', 'warning'); return; }

  if (appsmith.store.proj_bulk_busy) return;
  await storeValue('proj_bulk_busy', true);

  const failed = [];
  const deletedIds = [];

  try {
    for (let i = 0; i < rows.length; i++) {
      const id = rows[i]?.project_id;
      if (!id) continue;

      await storeValue('projDelId', id);

      try {
        const res = await del_project_pos.run();   // single source of truth
        const row = Array.isArray(res) ? res[0] : res;

        // If soft-deleted, just report; admin can clear links and retry later
        if (row?.action !== 'SOFT_DELETE') {
          deletedIds.push(id);
        }
      } catch (e2) {
        failed.push({ id, error: e2?.message || String(e2) });
      }
    }

    // If the currently selected project was deleted, clear page state
    const currentId = appsmith.store.selectedProjectData?.project_id;
    if (currentId && deletedIds.some(d => String(d) === String(currentId))) {
      await storeValue('selectedProjectData', null);
      await storeValue('proj_id', null);
    }

    await get_projects.run();
    await get_po_preview.run();
    await get_purchase_orders_update.run();
    await get_project_po_total.run();

    closeModal('j_proj_bulk_del_m');

    if (failed.length) {
      showAlert(`Deleted ${deletedIds.length} project(s). ${failed.length} failed.`, 'warning');
    } else {
      showAlert(`Deleted ${deletedIds.length} project(s).`, 'success');
    }
  } catch (e) {
    showAlert('Bulk delete failed: ' + (e?.message || e), 'error');
  } finally {
    await storeValue('proj_bulk_busy', false);
    await storeValue('projBulkRows', null);
    await storeValue('projDelId', null);
  }
})()
}}
