onClick:
{{
(async () => {
  const id =
    appsmith.store.projDelId
    || appsmith.store.projDelRow?.project_id
    || appsmith.store.selectedProjectData?.project_id;

  if (!id) { showAlert('Missing project id', 'error'); return; }
  if (appsmith.store.proj_del_busy) return;

  await storeValue('projDelId', id);
  await storeValue('proj_del_busy', true);

  try {
    const res = await del_project_pos.run();   // single source of truth
    const row = Array.isArray(res) ? res[0] : res;

    if (row?.action === 'SOFT_DELETE') {
      showAlert(
        `Project soft-deleted (linked in ${row.invoice_links} invoice(s) and ${row.billed_po_links} billed PO link(s)). Remove links first to hard delete.`,
        'warning'
      );
    } else {
      showAlert('Project and related POs deleted', 'success');
    }

    if (String(appsmith.store.selectedProjectData?.project_id) === String(id)) {
      await storeValue('selectedProjectData', null);
      await storeValue('proj_id', null);
    }

    await get_projects.run();
    closeModal('j_proj_del_m');
    closeModal('j_proj_upd_m');
  } catch (e) {
    showAlert('Delete failed: ' + (e?.message || e), 'error');
  } finally {
    await storeValue('proj_del_busy', false);
    await storeValue('projDelRow', null);
    await storeValue('projDelId', null);
  }
})()
}}
