{{
  (function(){
    const r = get_proj_guard.data?.[0] || {};
    const p = appsmith.store.projDel || {};

    const poTotal     = Number(r.po_count || 0);
    const billedPOs   = Number(r.billed_po_count || 0);
    const linkPOs     = Number(r.bill_link_count || 0);
    const billedOrLinked = billedPOs + linkPOs;

    const billIds = (r.bill_ids && r.bill_ids.length) ? r.bill_ids : '(none)';

    const invCount = Number(r.invoice_link_count || 0);
    const invIds   = (r.invoice_ids && r.invoice_ids.length) ? r.invoice_ids : '(none)';
    const invFlag  = invCount > 0 ? 'Yes' : 'No';

    return `• Project: ${p.name || ''} (ID ${p.project_id || ''})
• Purchase Orders: total ${poTotal}
• Billed/linked POs: ${billedOrLinked} (billed ${billedPOs}, linked ${linkPOs})
• Bill IDs: ${billIds}
• Invoiced: ${invFlag}  (Invoice IDs: ${invIds})

`;
  })()
}}
