onOptionChange:
{{
(async () => {
  // Reset dependent account dropdown
  resetWidget('i_customer_account_id', true);

  // Run the two lookups in parallel
  await Promise.all([
    get_customer_account_dropdown.run(),
    get_cust_curr_ins.run()
  ]);

  // Ensure currency options are loaded
  if (!Array.isArray(get_currencies_dropdown.data) || !get_currencies_dropdown.data.length) {
    await get_currencies_dropdown.run();
  }

  // Pick the customer's default currency from the query
  const row = (get_cust_curr_ins.data && get_cust_curr_ins.data[0]) || {};
  const curId = row.value ?? row.currency_id;

  if (curId != null) {
    // Try to match against the dropdown options (string/number safe)
    const opts = get_currencies_dropdown.data || [];
    const match = opts.find(o => String(o.value) === String(curId));

    if (match) {
      i_currency_id.setSelectedOption(match.value, match.label);
      await storeValue('selected_currency_quote', match.currency_quote ?? 1);
    } else {
      // Fallback (in case shapes differ)
      i_currency_id.setSelectedOption(curId, row.label ?? String(curId));
      await storeValue('selected_currency_quote', row.currency_quote ?? 1);
    }
  }
})()
}}
