onClick:
{{
  SubmitShield.guard() &&
  ins_proj.run(
    async () => {
      // Try to get insertId from the insert query result
      const resIns = ins_proj.data;
      const insertId =
        (Array.isArray(resIns) && resIns[0]?.insertId) ||
        resIns?.insertId ||
        null;

      // Fallback to a separate SELECT if the driver doesn't expose insertId
      let projectId = insertId;
      if (!projectId) {
        const lastRows = await GetLastProjectID.run();   // <-- returns rows directly
        projectId = lastRows?.[0]?.project_id || null;
      }

      if (!projectId) {
        showAlert('Could not fetch new project id', 'error');
        return;
      }

      // Keep stores in sync for downstream queries
      await storeValue('proj_id', projectId);
      await storeValue('selectedProjectData', { project_id: projectId });

      await get_projects.run();               // refresh table datasource
      closeModal('j_proj_ins_m');

      await get_proj_by_id.run();             // loads by appsmith.store.proj_id
      showModal('j_proj_upd_m');              // open update modal
    },
    (e) => showAlert('Insert failed: ' + (e?.message || e), 'error')
  )
}}
