backgroundColor:
{{
  (() => {
    const revenue = Number(u_project_total.text || 0) / (appsmith.store.selected_currency_quote || 1);
    const expenses = update_purchase_order_table.tableData.reduce((sum, row) => sum + (row.total || 0), 0) / (appsmith.store.selected_currency_quote || 1);
    if (revenue === 0) return "#ff4d4f"; // red
    const margin = ((revenue - expenses) / revenue) * 100;

    if (margin > 45) return "#73d13d";        // green
    if (margin >= 20 && margin <= 45) return "#ffc53d"; // yellow
    return "#ff4d4f";                         // red
  })()
}}

text:
{{
  (() => {
    const revenue = Number(u_project_total.text || 0) / (appsmith.store.selected_currency_quote || 1);
    const expenses = update_purchase_order_table.tableData.reduce((sum, row) => sum + (row.total || 0), 0) / (appsmith.store.selected_currency_quote || 1);
    if (revenue === 0) return "0%";
    const margin = ((revenue - expenses) / revenue) * 100;
    return margin.toFixed(2) + "%";
  })()
}}
