onClick:
{{
  // Always open blank, then fill project & USD currency defaults
  resetWidget('j_po_ins_m', true);

  // Keep store in sync (proj_id used by queries downstream)
  storeValue('updateMode', false)
    .then(() => {
      // If you're using a JSON Form in PO insert modal:
      //   - Name it j_po_ins_form
      //   - Ensure it has a field `project_id` and `currency_id`
      //   - Set their initial values right after reset:
      try {
        // OPTION A: JSON Form (recommended)
        j_po_ins_form.setFormData({
          ...(j_po_ins_form.formData || {}),
          project_id: appsmith.store.proj_id, // link PO â†’ current project
          currency_id: 1                      // USD only for POs per your standard
        });
      } catch(e) {
        // OPTION B: individual widgets (if not using JSON Form)
        // Ensure you actually have these widget names inside j_po_ins_m:
        // p_project_id (Input/hidden), p_currency_id (Select/Dropdown)
        p_project_id.setValue(appsmith.store.proj_id);
        p_currency_id.setSelectedOption({ label: 'US Dollar', value: 1 });
      }

      // If you keep currency fixed for POs, disable the widget:
      // p_currency_id.setDisabled(true);  // or in JSON Form, set disabled for the field

      showModal('j_po_ins_m');
    });
}}
