onClick:
{{
  (async () => {
    // 1) Store prefill from Project Update widgets
    await storeValue('poInsPrefill', {
      service_id: u_service_id.selectedOptionValue ?? null,
      unit_id: u_unit_id.selectedOptionValue ?? null,
      source_language_id: u_source_language_id.selectedOptionValue ?? null,
      target_language_id: u_target_language_id.selectedOptionValue ?? null,
      pricing_mode: u_pricing_mode.selectedOptionValue ?? null,
      volume: u_project_volume.text ?? null
    });

    // 2) Reset PO insert widgets (so defaults re-apply cleanly)
    try { resetWidget('p_service_id', true); } catch (e) {}
    try { resetWidget('p_unit_id', true); } catch (e) {}
    try { resetWidget('p_source_language_id', true); } catch (e) {}
    try { resetWidget('p_target_language_id', true); } catch (e) {}
    try { resetWidget('p_pricing_mode', true); } catch (e) {}
    try { resetWidget('p_volume', true); } catch (e) {}

    // 3) Open modal
    showModal('j_po_ins_m');
  })()
}}
