customColumn1.onClick:
{{
(async () => {
  try {
    const row = currentRow;
    if (!row || !row.purchase_order_id) { showAlert('Missing PO row', 'error'); return; }

    const toAddr = (row.resource_email || '').trim();
    if (!toAddr) { showAlert('Resource email is missing on this row.', 'warning'); return; }

    // Load the po_email template
    await get_po_email.run();
    const templ = ((get_po_email.data || [])[0]) || {};

    // Row data
    const poId  = row.purchase_order_id;
    const name  = row.resource_name || 'Resource';
    const proj  = row.project_name || '';
    const svc   = row.service_name || '';
    const src   = row.source_language_name || '';
    const tgt   = row.target_language_name || '';
    const unit  = row.unit_name || '';
    const vol   = Number(row.volume ?? 0);
    const rate  = Number(row.rate ?? 0);
    const total = (row.total != null ? Number(row.total) : vol * rate);
    const cur   = row.currency_name || row.currency_acronym || '';
    const due   = row.due_date || row.date || '';

    const fmt3 = (n) => Number(n || 0).toFixed(3);

    // Pick template fields (robust to different column names)
    const tplSubject = templ.template_email_subject || templ.email_subject || templ.subject || '';
    const tplBody    = templ.template_email_body_html || templ.body_html || templ.body || '';
    const tplHeader  = templ.template_header_html || templ.header_html || '';
    const tplFooter  = templ.template_footer_html || templ.footer_html || '';
    const tplBcc     = templ.template_email_bcc_field || templ.template_email_bcc || templ.bcc || '';

    // Simple {{var}} replacement
    const map = {
      po_id: poId,
      project_name: proj,
      service_name: svc,
      source_language_name: src,
      target_language_name: tgt,
      unit_name: unit,
      volume: fmt3(vol),
      rate: `${fmt3(rate)} ${cur}`,
      total: `${fmt3(total)} ${cur}`,
      due_date: (due || '-'),
      resource_name: name,
      resource_email: toAddr
    };
    const replaceVars = (text) => {
      if (!text) return '';
      let out = String(text);
      Object.keys(map).forEach(k => {
        const re = new RegExp('{{\\s*' + k + '\\s*}}', 'gi');
        const re2 = new RegExp('{{\\s*' + k.toUpperCase() + '\\s*}}', 'g');
        out = out.replace(re, map[k]).replace(re2, map[k]);
      });
      return out;
    };

    const subject = replaceVars(tplSubject || `Purchase Order #${poId} - ${proj}`);
    const header  = replaceVars(tplHeader);
    const body    = replaceVars(tplBody);
    const footer  = replaceVars(tplFooter);

    const detailsTable = `
      <table cellpadding="6" cellspacing="0" border="0" style="border-collapse:collapse;">
        <tr><td><b>PO #</b></td><td>${poId}</td></tr>
        <tr><td><b>Project</b></td><td>${proj}</td></tr>
        <tr><td><b>Service</b></td><td>${svc}</td></tr>
        <tr><td><b>Languages</b></td><td>${src} â†’ ${tgt}</td></tr>
        <tr><td><b>Unit</b></td><td>${unit}</td></tr>
        <tr><td><b>Volume</b></td><td>${fmt3(vol)}</td></tr>
        <tr><td><b>Rate</b></td><td>${fmt3(rate)} ${cur}</td></tr>
        <tr><td><b>Total</b></td><td><b>${fmt3(total)} ${cur}</b></td></tr>
        <tr><td><b>Due date</b></td><td>${due || '-'}</td></tr>
      </table>
    `;

    const html = `
      <html><body style="font-family:Arial, sans-serif; line-height:1.5;">
        ${header}
        ${body}
        ${detailsTable}
        ${footer}
      </body></html>
    `;

    // Outlook Web compose deep link (supports HTML body)
    const params = new URLSearchParams();
    params.set('to', toAddr);
    if (tplBcc) params.set('bcc', tplBcc);
    params.set('subject', subject);
    params.set('body', html);
    params.set('bodyIsHtml', 'true');

    const outlookCompose = 'https://outlook.office.com/mail/deeplink/compose?' + params.toString();

    // Fallback to mailto (plain text) if URL too long
    if (outlookCompose.length < 1800) {
      navigateTo(outlookCompose, {}, 'NEW_WINDOW');
    } else {
      const plain = [
        `Dear ${name},`,
        ``,
        `Please find the Purchase Order details below:`,
        `PO #${poId} â€” ${proj}`,
        `Service: ${svc}`,
        `Languages: ${src} -> ${tgt}`,
        `Unit: ${unit}`,
        `Volume: ${fmt3(vol)}`,
        `Rate: ${fmt3(rate)} ${cur}`,
        `Total: ${fmt3(total)} ${cur}`,
        `Due date: ${due || '-'}`,
        ``,
        `Best regards,`,
        `${appsmith.user?.name || ''}`
      ].join('\n');

      const mailto = `mailto:${encodeURIComponent(toAddr)}?subject=${encodeURIComponent(subject)}&body=${encodeURIComponent(plain)}`;
      navigateTo(mailto, {}, 'NEW_WINDOW');
      showAlert('Opened standard mail client (plain text body).', 'warning');
    }
  } catch (e) {
    showAlert('Send email error: ' + (e?.message || e), 'error');
  }
})()
}}

customColumn4.onClick:
{{
  (async () => {
    await storeValue('rpo_id', update_purchase_order_table.triggeredRow.purchase_order_id);

    // Try cache; fallback to direct runs if cache isn't available
    try {
      if (OptionsCache?.loadOnce) {
        await OptionsCache.loadOnce();      // fast after first run
      } else {
        await Promise.all([
          get_resources_dd.run(),
          get_services_dd.run(),
          get_units_dd.run(),
          get_languages_dd.run()
        ]);
        await storeValue('opt_resources', get_resources_dd.data || []);
        await storeValue('opt_services',  get_services_dd.data  || []);
        await storeValue('opt_units',     get_units_dd.data     || []);
        await storeValue('opt_langs',     get_languages_dd.data || []);
      }
    } catch (e) {
      // In case any of the above fail, do a minimal fallback so the modal still opens
      await Promise.allSettled([
        get_resources_dd.run(),
        get_services_dd.run(),
        get_units_dd.run(),
        get_languages_dd.run()
      ]);
    }

    // Load the PO row (includes resource_email from the JOIN)
    const ok = await get_rpo_by_id.run();
    if (!ok || !get_rpo_by_id.data?.length) {
      showAlert('Could not load purchase order details', 'error');
      return;
    }

    // Fill widgets (uses cached arrays + DB row) and open modal
    await POFormLoader.fillUpdate(get_rpo_by_id.data[0]);
    showModal('j_po_upd_m');
  })()
}}
