onClick:
{{
  (async () => {
    if (!appsmith.store.resourceBankingRow?.resource_banking_id) {
      showAlert('No row selected', 'warning');
      return;
    }

    await del_rb.run();                       // run the DELETE
    showAlert('Banking info deleted', 'success');

    await get_rb_list.run();                  // refresh the table
    closeModal('Confirm_Delete_Modal');       // close confirm
    await storeValue('resourceBankingRow', null);  // clean up
  })()
}}
