onClick:
{{
  (async () => {
    // ---- Guards: don't run without context/selection
    const billId = appsmith.store.billRow?.resource_bill_id;
    const sel = unbilled_po_table_new.selectedRows || [];
    if (!billId) { showAlert('Missing bill context. Reopen the Update modal or pick a bill again.', 'warning'); return; }
    if (!sel.length) { showAlert('Select at least one PO.', 'warning'); return; }

    // Optional: ensure we actually have numeric PO IDs
    const ids = sel.map(r => Number(r.purchase_order_id)).filter(Boolean);
    if (!ids.length) { showAlert('No valid PO ids in selection.', 'warning'); return; }

    // ---- Do the work, step by step, and stop exactly where it fails
    try {
      // 1) Link POs to the bill
      try { await ins_bill_links_json.run(); }
      catch (e) { showAlert('Linking POs failed (ins_bill_links_json): ' + (e?.message || e), 'error'); return; }

      // 2) Mark POs as billed (if you still use this; keep, since it exists on your page)
      try { await upd_pos_set_billed_json.run(); }
      catch (e) { showAlert('Marking POs billed failed (upd_pos_set_billed_json): ' + (e?.message || e), 'error'); return; }

      // 3) Update the bill's PO list string
      try { await upd_bill_po_list.run(); }
      catch (e) { showAlert('Updating bill PO list failed (upd_bill_po_list): ' + (e?.message || e), 'error'); return; }

      // 4) Refresh both tables
      try { await get_bill_purchase_orders.run(); } catch (e) { /* not fatal */ }
      try { await get_unb_pos_by_res.run(); } catch (e) { /* not fatal */ }

      // 5) Close and return to Update modal
      closeModal('Add_Purchase_Order_Modal');
      showModal('Update_Modal');
      showAlert('POs linked to bill.', 'success');
    } catch (e) {
      showAlert('Add POs failed: ' + (e?.message || e), 'error');
    }
  })()
}}
