onClick:
{{
  (async () => {
    if (!new_resource_id.selectedOptionValue) { showAlert('Choose a resource', 'warning'); return; }
    if (!new_bill_date.selectedDate) { showAlert('Choose bill date', 'warning'); return; }
    if (!new_due_date.selectedDate) { showAlert('Choose due date', 'warning'); return; }

    await insert_resource_bill.run();

    await get_last_resource_bill_id.run();
    const newId = get_last_resource_bill_id.data[0]?.last_id;
    if (!newId) { showAlert('Could not get new bill id', 'error'); return; }

    const billRow = {
      resource_bill_id: newId,
      resource_id: new_resource_id.selectedOptionValue,
      resource_email: new_resource_email.text || '',
      resource_bill_date: new_bill_date.selectedDate ? moment(new_bill_date.selectedDate).format('YYYY-MM-DD') : null,
      resource_bill_due_date: new_due_date.selectedDate ? moment(new_due_date.selectedDate).format('YYYY-MM-DD') : null
    };
    await storeValue('billRow', billRow);

    await get_resource_bills.run();
    closeModal('New_Modal');
    showModal('Update_Modal');

    const r = (get_resources_dd.data || []).find(x => x.resource_id == billRow.resource_id);
    upd__resource_id.setSelectedOption({ label: r ? r.resource_full_name : '', value: billRow.resource_id });
    upd_resource_email.setValue(r ? (r.resource_email || '') : '');
    upd_bill_date.setValue(billRow.resource_bill_date || '');
    upd_due_date.setValue(billRow.resource_bill_due_date || '');

    await storeValue('updPOs', []);
  })()
}}
