onClick:
{{
  (async () => {
    if (appsmith.store.poDelBusy) return;
    await storeValue('poDelBusy', true);
    try {
      const g = await get_po_del_guard.run();
      const r = (g && g[0]) || {};

      // 1) Project restriction with project name
      if (Number(r.in_project) === 1) {
        showAlert(
          `This PO is linked to project "${r.project_name}". Remove it from the project first.`,
          'warning'
        );
        return;
      }

      // 2) Keep your existing bill/link protection (still enforced)
      if (Number(r.in_bill_status) === 1 || Number(r.in_bill_link) === 1) {
        showAlert('This PO is already in or linked to a Bill. Unlink it first.', 'warning');
        return;
      }

      // 3) Proceed with delete
      await del_po.run();

      // 4) Refresh the table
      await get_purchase_orders.run();   // your page list action
      // (It already returns project_name; no changes needed.)  // ref
      closeModal('j_po_del_m');
      showAlert('PO deleted','success');
    } catch (e) {
      showAlert('Delete error: ' + (e?.message || e), 'error');
    } finally {
      await storeValue('poDelBusy', false);
    }
  })()
}}
