onClick:
{{
(() => {
const rows = tbl_resources.selectedRows || [];
if (!rows.length) { showAlert('Select at least one resource.', 'warning'); return; }
const emails = [...new Set(rows.map(r => (r.resource_email || '').trim()).filter(Boolean))];
if (!emails.length) { showAlert('No emails found.', 'warning'); return; }
const ds = get_email_templates_dd_res.data || [];
const val = String(i_email_template_res.selectedOptionValue || '');
const hit = ds.find(o => String(o.value) === val) || {};
const subject = String(hit.template_email_subject || '');
const html = String(hit.template_email_body_html || '');
const ctx = tbl_resources.selectedRow || rows[0] || {};
const render = s => String(s).replace(/\{\{\s*(\w+)\s*\}\}/g, (m,k)=> (k in ctx ? String(ctx[k] ?? '') : m));
const subjectR = render(subject);
const bodyTxt = render(html)
.replace(/<br\s*\/?\s*>/gi, '\n')
.replace(/<\/p>/gi, '\n\n')
.replace(/<p\b[^>]*>/gi, '')
.replace(/<[^>]+>/g, '')
.replace(/\n{3,}/g, '\n\n')
.trim() || '\n';
const bcc = emails.map(e => e.replace(/[\s,;]+/g,'')).join(';');
const params = [];
if (subjectR) params.push('subject=' + encodeURIComponent(subjectR));
params.push('bcc=' + bcc);
params.push('body=' + encodeURIComponent(bodyTxt));
params.push('x=1');
const url = 'mailto:?' + params.join('&');
navigateTo(url,'NEW_WINDOW');
})()
}}
