onClick:
{{
(() => {
  // Reset all three widgets
  i_res_status.setSelectedOptions([]);   // multi-select
  i_lang_search.setValue('');            // input
  i_res_q.setValue('');                  // input

  // Clear store values used in filters
  storeValue('resStatus', []);
  storeValue('resStatusHasEmpty', false);
  storeValue('langQ', '');
  storeValue('resQ', '');

  // Refresh main table query
  return toggle_show_inactive_res.isSwitchedOn
    ? get_resources_all.run()
    : get_resources_list.run();
})()
}}
