onClick:
{{
(async () => {
  const rows = appsmith.store.resBulkSel || [];
  const toDel = rows.filter(r => String(r.resource_is_active ?? 1) !== '0');
  if (!toDel.length) { showAlert('Nothing to archive (already inactive).', 'info'); closeModal('j_res_bulk_del_m'); return; }

  await storeValue('_res_bulk_del_busy', true);
  try {
    await Promise.allSettled(
      toDel.map(r => upd_resource_soft_del.run({ resource_id: Number(r.resource_id) }))
    );

    await get_resources_list.run();
    if (typeof tbl_resources.clearSelection === 'function') { tbl_resources.clearSelection(); }

    showAlert(`Archived ${toDel.length} resource(s).`, 'success');
    closeModal('j_res_bulk_del_m');
  } catch (e) {
    showAlert('Bulk delete failed: ' + (e?.message || e), 'error');
  } finally {
    await storeValue('_res_bulk_del_busy', false);
    await storeValue('resBulkSel', null);
  }
})()
}}
