onClick:
{{
(async () => {
  const id = appsmith.store.resDelRow?.resource_id;
  if (!id) { showAlert('Missing resource id.', 'error'); return; }

  try {
    await upd_resource_soft_del.run({ resource_id: Number(id) });
    await get_resources_list.run();

    // clear selection if your version supports it
    if (typeof tbl_resources.clearSelection === 'function') { tbl_resources.clearSelection(); }

    showAlert('Resource archived.', 'success');
    closeModal('j_res_del_m');
    await storeValue('resDelRow', null);
  } catch (e) {
    showAlert('Delete failed: ' + (e?.message || e), 'error');
  }
})()
}}
