onClick:
{{
(async () => {
  const rid = appsmith.store.resRow?.resource_id || tbl_resources.selectedRow?.resource_id;
  const src = u_src_lang_add_upd.selectedOptionValue;
  const tgt = u_tgt_lang_add_upd.selectedOptionValue;
  if (!rid || !src || !tgt) { showAlert('Pick resource, source and target.', 'warning'); return; }
  if (String(src) === String(tgt)) { showAlert('Source and target cannot be the same.', 'warning'); return; }

  try {
    // 1) Reactivate if exists inactive
    await upd_res_pair_reac.run({
      resource_id: Number(rid),
      source_language_id: Number(src),
      target_language_id: Number(tgt)
    });
    // 2) Insert if brand new
    await ins_res_pair_if_new.run({
      resource_id: Number(rid),
      source_language_id: Number(src),
      target_language_id: Number(tgt)
    });
    // 3) Refresh list + clear pickers
    await get_res_pairs.run();
    resetWidget('u_src_lang_add_upd', true);
    resetWidget('u_tgt_lang_add_upd', true);
    showAlert('Pair added / reactivated.', 'success');
  } catch (e) {
    showAlert('Add pair failed: ' + (e?.message || e), 'error');
  }
})()
}}
