onClick:
{{
(async () => {
  const teamId = i_wt_pick.selectedOptionValue;
  const sel = tbl_resources.selectedRows || [];
  if (!teamId) { showAlert('Pick a team first.', 'warning'); return; }
  if (!sel.length) { showAlert('Select at least one resource.', 'warning'); return; }

  try {
    // re-activate + insert (your existing queries/names)
    const resUpd = await upd_wtm_reac_from_resources.run();
    const resIns = await ins_wtm_bulk_from_resources.run();

    // refresh main list (optional) + the new preview table
    await get_resources_list.run();
    await get_resource_teams.run();   // ‚Üê refresh the new preview table

    const reactivated =
      Number(resUpd?.rowsAffected ?? resUpd?.affectedRows ?? resUpd?.[0]?.affectedRows ?? 0);
    const inserted =
      Number(resIns?.rowsAffected ?? resIns?.affectedRows ?? resIns?.[0]?.affectedRows ?? 0);

    showAlert(`Added to team: ${inserted}. Reactivated: ${reactivated}.`, 'success');
  } catch (e) {
    showAlert('Bulk add failed: ' + (e?.message || e), 'error');
  }
})();
}}
