onClick:
{{
(() => {
  var sel = i_email_template_res.selectedOptionValue;
  if (!sel) { showAlert('Please choose a template','error'); return; }

  var rows = tbl_resources.selectedRows || [];
  if (!rows.length) { showAlert('Please select at least one resource','error'); return; }

  var tpl = (get_tpl_by_id_res.data && get_tpl_by_id_res.data[0]) || null;
  if (!tpl) { showAlert('Template not loaded yet. Pick the template again.','error'); return; }

  var team = (typeof sel_wt_filter !== 'undefined' && sel_wt_filter.selectedOptionLabel) 
             || (rows[0] && rows[0].work_team_name) || '';

  var expand = function(str, row) {
    if (!str) return '';
    var out = String(str);
    var name = (row.resource_full_name || row.resource_name || '');
    out = out.replace(/\{resource_name\}/g, name)
             .replace(/\{resource_email\}/g, row.resource_email || '')
             .replace(/\{resource_alias_email\}/g, row.resource_alias_email || '')
             .replace(/\{work_team_name\}/g, team || '');
    return out;
  };

  var bccTpl = tpl.template_email_bcc_field || '{resource_email}';
  var bccList = [];
  for (var i = 0; i < rows.length; i++) {
    var expanded = expand(bccTpl, rows[i]);                 // e.g. "{resource_email};{resource_alias_email}"
    var parts = expanded.split(/[;,]/).map(s => s.trim()).filter(Boolean);
    bccList = bccList.concat(parts);
  }
  var seen = {};
  var bcc = bccList.filter(e => { if (seen[e]) return false; seen[e] = 1; return true; }).join(';');

  var first = rows[0] || {};
  var subject = expand(tpl.template_email_subject || '', first);
  var bodyHtml = expand(tpl.template_email_body_html || '', first);

  // mailto prefers plain text; convert minimal HTML -> text
  var bodyText = bodyHtml
    .replace(/<p>/gi, '\n')
    .replace(/<\/p>/gi, '\n')
    .replace(/<br\s*\/?>/gi, '\n')
    .replace(/<[^>]*>/g, '')
    .trim();

  var url = 'mailto:?bcc=' + encodeURIComponent(bcc)
    + '&subject=' + encodeURIComponent(subject)
    + '&body=' + encodeURIComponent(bodyText);

  navigateTo(url, {}, 'NEW_WINDOW');
})()
}}
