onClick:
{{
  (() => {
    const r = tbl_resources.selectedRow || tbl_resources.triggeredRow;
    if (!r) { 
      showAlert("Select one resource row.", "warning"); 
      return; 
    }

    const t = (get_tpl_by_id_res.data && get_tpl_by_id_res.data[0])
      ? get_tpl_by_id_res.data[0]
      : null;

    if (!t) { 
      showAlert("Select a template (and wait for it to load).", "warning"); 
      return; 
    }

    const to = (TplRender.render(t.to_field) || "").trim() 
      || (r.resource_email || "").trim();

    const cc = (TplRender.render(t.cc_field) || "").trim();
    const bcc = (TplRender.render(t.bcc_field) || "").trim();
    const subject = (TplRender.render(t.subject_field) || "").trim();
    const body = (TplRender.render(t.body_field) || "").toString();

    if (!to) { 
      showAlert("No recipient email found.", "warning"); 
      return; 
    }

    // IMPORTANT:
    // Do NOT use URLSearchParams for Outlook body
    // encodeURIComponent keeps spaces as %20 (no "+" bug)
    const q = [];
    if (cc) q.push("cc=" + encodeURIComponent(cc));
    if (bcc) q.push("bcc=" + encodeURIComponent(bcc));
    if (subject) q.push("subject=" + encodeURIComponent(subject));
    q.push("body=" + encodeURIComponent(body || "\n"));

    const url =
      "mailto:" + encodeURIComponent(to) + "?" + q.join("&");

    navigateTo(url, {}, "NEW_WINDOW");
  })()
}}
