(async () => {
  const row =
    (tbl_resources.selectedRows && tbl_resources.selectedRows[0])
    || appsmith.store.resRow
    || tbl_resources.selectedRow;

  if (!row?.resource_id) {
    showAlert('No resource selected.', 'warning');
    return;
  }

  await storeValue('resRow', row);

  // IMPORTANT: refresh pairs every time you open update
  await get_res_pairs.run();

  showModal('j_res_upd_m');
})()
}}

