{{
  (async () => {
    const row =
      appsmith.store.resRow
      || (Array.isArray(tbl_resources.selectedRows) && tbl_resources.selectedRows.length ? tbl_resources.selectedRows[0] : null)
      || tbl_resources.selectedRow
      || (Array.isArray(appsmith.store._res_sel) && appsmith.store._res_sel.length ? appsmith.store._res_sel[0] : null);

    if (!row?.resource_id) { showAlert('No resource selected.', 'warning'); return; }

    await storeValue('resDelRow', row);
    showModal('j_res_del_m');
  })()
}}

