{{
  (() => {
    const rows = tbl_resources.selectedRows || [];
    if (!rows.length) { showAlert("Select at least one resource.", "warning"); return; }

    const emails = [...new Set(
      rows.map(r => String(r.resource_email || "").trim()).filter(Boolean)
    )];

    if (!emails.length) { showAlert("No emails found in selected rows.", "warning"); return; }

    const params = new URLSearchParams({
      bcc: emails.join(";"),
      subject: "",
      body: "\n"
    });

    const url = "mailto:?" + params.toString();
    navigateTo(url, {}, "NEW_WINDOW");
  })()
}}

