onClick:
{{
(async () => {
  const idRaw = appsmith.store.resDelRow?.resource_id;
  const id = Number(idRaw);

  if (!id || Number.isNaN(id)) {
    showAlert('Missing/invalid resource id.', 'warning');
    return;
  }

  await upd_resource_soft_del.run({ resource_id: id });

  if (toggle_show_inactive_res.isSwitchedOn) {
    await get_resources_all.run();
  } else {
    await get_resources_list.run();
  }

  closeModal('j_res_del_m');
  showAlert('Resource deleted (inactive).', 'success');
})()
}}
