onClick:
{{
  (() => {
    const roleId = Number(Role_DD.selectedOptionValue);
    const ids = (Resource_MS.selectedOptionValues || []).map(Number).filter(Boolean);
    if (!roleId) { showAlert('Pick a role first.', 'warning'); return; }
    if (!ids.length) { showAlert('Select at least one resource.', 'warning'); return; }

    assign_user_role.run(
      () => {
        // refresh the user/resources table if present
        const refreshUsers = (typeof get_users !== 'undefined' && get_users.run)
          ? get_users.run
          : (typeof get_resources_test !== 'undefined' && get_resources_test.run)
              ? get_resources_test.run
              : null;

        const afterUsers = () => get_resource_roles_summary.run(
          () => get_resource_roles_flat.run(
            () => { 
              showAlert('Role assigned to selected resources', 'success');
              closeModal('j_assign_role_modal');
            },
            (e)=>showAlert('resource-roles flat failed: '+(e?.message||e), 'error')
          ),
          (e)=>showAlert('resource-roles summary failed: '+(e?.message||e), 'error')
        );

        refreshUsers ? refreshUsers(afterUsers, (e)=>{ showAlert('Users refresh failed: '+(e?.message||e),'error'); afterUsers(); })
                     : afterUsers();
      },
      (e)=>showAlert('Assign failed: '+(e?.message||e), 'error')
    );
  })()
}}
