onClick:
{{
  (() => {
    const roleId = Number(appsmith.store.roleRow?.role_id);
    if (!roleId) { showAlert('Pick a role first.', 'warning'); return; }

    const selected = (ms_role_perms.selectedOptionValues || []).map(Number).filter(Boolean);

    // 1) Clear all permissions for this role
    del_role_permissions_by_role.run(
      () => {
        // 2) If nothing selected, just refresh and close
        if (!selected.length) {
          get_role_perms.run(
            () => get_roles_perms_summary.run(
              () => get_roles_perms_flat.run(
                () => get_resource_roles_summary.run(
                  () => get_resource_roles_flat.run(
                    () => { showAlert('Permissions cleared','success'); closeModal('j_role_permissions_modal'); }
                  )
                )
              )
            )
          );
          return;
        }

        // 3) Bulk insert (prepared OFF on the query)
        ins_role_permissions_bulk.run(
          () => {
            get_role_perms.run(
              () => get_roles_perms_summary.run(
                () => get_roles_perms_flat.run(
                  () => get_resource_roles_summary.run(
                    () => get_resource_roles_flat.run(
                      () => { showAlert('Permissions saved','success'); closeModal('j_role_permissions_modal'); }
                    )
                  )
                )
              )
            );
          },
          (e) => showAlert('Insert permissions failed: ' + (e?.message || e), 'error')
        );
      },
      (e) => showAlert('Delete permissions failed: ' + (e?.message || e), 'error')
    );
  })()
}}
