onClick:
{{
  (async () => {
    const cid = tbl_customers.selectedRow?.customer_id || appsmith.store.sel_cust_id;

    if (!cid) {
      showAlert('Select a customer first.', 'warning');
      return;
    }

    await storeValue('sel_cust_id', cid);

    await storeValue('ctInsObj', {
      customer_contact_name: null,
      contact_position: null,
      customer_contact_email: null,
      customer_id: cid,
      customer_contact_industry: null,
      customer_contact_website: null,
      customer_contact_linkedin: null,
      customer_contact_first_contact: null,
      customer_contact_last_contact: null,
      customer_contact_next_contact: null,
      customer_contact_contact_method: null,
      customer_contact_test: null,
      customer_contact_notes: null,
      contact_is_active: 1
    });

    resetWidget('jf_ct_ins', true);
    showModal('m_ct_ins');
  })()
}}
