onClick:
{{
  (async () => {
    try {
      // Ensure dropdown data is loaded only if missing
      if (!Array.isArray(get_customers.data)) {
        await get_customers.run();
      }

      // Clean insert form (guaranteed fresh state)
      resetWidget('jf_cust_ins', true);

      // Optional: clear selected customer context to avoid side effects
      await storeValue('sel_cust_row', null);

      // Open insert modal
      showModal('m_cust_ins');

    } catch (e) {
      showAlert(e?.message || 'Failed to open Add Customer modal.', 'error');
    }
  })()
}}
