onClick:
{{
  (function () {
    const key = 'serv_del';
    if (!SubmitShield.lock(key)) return;

    del_service.run(
      async () => {
        await get_services_list.run();   // refresh table
        SubmitShield.release(key);
        closeModal('j_serv_del_m');
        showAlert('Service deleted.', 'success');
      },
      (err) => {
        SubmitShield.release(key);
        closeModal('j_serv_del_m');
        showAlert('Delete failed: ' + (err?.message || JSON.stringify(err)), 'error');
      }
    );
  })()
}}
