onClick:
{{
  (async () => {
    const v = i_tpl_var_picker.selectedOptionValue;
    if (!v) { showAlert('Pick a placeholder first', 'warning'); return; }

    const token = `{{${v}}}`;
    const type  = String(j_tpl_ins_form.formData?.template_type || '');

    if (/email/i.test(type)) {
      const current = j_tpl_ins_form.formData?.template_email_body_html || '';
      await storeValue('tpl_ins_data', {
        ...(appsmith.store.tpl_ins_data || {}),
        template_email_body_html: current + token
      });
    } else if (/pdf/i.test(type)) {
      const current = j_tpl_ins_form.formData?.template_pdf_body || '';
      await storeValue('tpl_ins_data', {
        ...(appsmith.store.tpl_ins_data || {}),
        template_pdf_body: current + token
      });
    } else {
      showAlert('Set template type first (Email or PDF).', 'warning');
    }
  })()
}}
