onClick:
{{
  (async () => {
    const v = u_tpl_var_picker.selectedOptionValue;
    if (!v) { showAlert('Pick a placeholder first', 'warning'); return; }

    const token = `{{${v}}}`;
    const type  = String(j_tpl_upd_form.formData?.template_type || '');

    if (/email/i.test(type)) {
      const current = j_tpl_upd_form.formData?.template_email_body_html || '';
      await storeValue('tpl_upd_data', {
        ...(appsmith.store.tpl_upd_data || {}),
        template_email_body_html: current + token
      });
    } else if (/pdf/i.test(type)) {
      const current = j_tpl_upd_form.formData?.template_header_html || '';
      await storeValue('tpl_upd_data', {
        ...(appsmith.store.tpl_upd_data || {}),
        template_header_html: current + token
      });
    } else {
      showAlert('Set template type first (Email or PDF).', 'warning');
    }
  })()
}}
