menuItem1.onClick:
{{
(async () => {
  const row = tbl_wtm_creds_i.selectedRow;
  if (!row) { showAlert('Select a credential row first.', 'warning'); return; }

  await storeValue('ctx_parent', 'mgmt');       // return to Management panel after save
  await storeValue('wtmCredRow', row);          // the row to edit

  await get_uncreds_dd.run();                   // refresh pool
  resetWidget('i_uncred_pick_upd', true);
  resetWidget('j_wtm_cred_upd_form', true);

  showModal('j_wtm_cred_upd_m');                // open UPDATE modal
})();
}}

menuItem2.onClick:
{{
(() => {
  const r = tbl_wtm_creds_i.selectedRow;
  if (!r) { showAlert('Select a credential row first.', 'warning'); return; }
  const block =
`Username: ${r?.username || ''}
Password: ${r?.password_text || ''}
Alias Email: ${r?.alias_email || ''}`;
  copyToClipboard(block);
  showAlert('Credential block copied.', 'success');
})()
}}

menuItem3.onClick:
{{
(async () => {
  const row = tbl_wtm_creds_i.selectedRow;
  if (!row) { showAlert('Select a credential row first.', 'warning'); return; }

  try {
    // soft delete the member credential
    await del_wtm_credential_soft.run();

    // if it came from the pool, return it to available
    if (row.origin_uncred_id) { await uncred_return_from_member.run(); }

    // refresh UI
    await get_wtm_credentials.run();
    await get_uncreds_dd.run();

    showAlert('Credential removed.', 'success');
  } catch (e) {
    showAlert('Delete failed: ' + (e?.message || e), 'error');
  }
})();
}}
