menuItem2.onClick:
{{
(async () => {
  const row = tbl_wtm_links_i.selectedRow;
  if (!row) { showAlert('Select a link row first.', 'warning'); return; }

  await storeValue('ctx_parent', 'mgmt');    // return to inline panel after save
  await storeValue('wtmLinkRow', row);       // row to edit

  resetWidget('j_wtm_link_upd_form', true);
  showModal('j_wtm_link_upd_m');             // reuse existing update modal
})();
}}

menuItem3.onClick:
{{
(async () => {
  const row = tbl_wtm_links_i.selectedRow;
  if (!row) { showAlert('Select a link row first.', 'warning'); return; }

  try {
    await del_wtm_link_soft.run();   // query updated below to read selectedRow
    await get_wtm_links.run();       // refresh list
    showAlert('Link removed.', 'success');
  } catch (e) {
    showAlert('Delete failed: ' + (e?.message || e), 'error');
  }
})();
}}

