onClick:
{{
  (async () => {
    const teamId = appsmith.store.wtRow?.work_team_id;
    if (!teamId) { showAlert('Missing team context.', 'warning'); return; }

    try {
      await del_wt_links_by_team.run();
      await del_wt_creds_by_team.run();
      await del_wt_members_by_team.run();
      await del_work_team.run();

      // refresh the dataset currently shown
      if (toggle_show_archived_wt.isSwitchedOn) {
        await get_work_teams_all.run();
      } else {
        await get_work_teams_nonarchived.run();
      }

      closeModal('j_wt_del_m');
      showAlert('Team deleted.', 'success');
      await storeValue('wtRow', null);
    } catch (e) {
      showAlert('Error deleting team: ' + (e?.message || e), 'error');
    }
  })()
}}
