(() => {
  // Helper to resolve labels from dropdown datasets
  const pick = (arr, val) => (arr || []).find(o => String(o.value) === String(val))?.label || '';

  const fd  = j_wt_upd_form.formData || {};
  const det = get_work_team_detail.data?.[0] || {};

  const accId = fd.customer_account_id ?? det.customer_account_id;
  const srvId = fd.service_id          ?? det.service_id;
  const uniId = fd.unit_id             ?? det.unit_id;
  const srcId = fd.source_language_id  ?? det.source_language_id;
  const tgtId = fd.target_language_id  ?? det.target_language_id;

  const acc = pick(get_cust_acc_dd_upd.data, accId);
  const srv = pick(get_services_dd.data,     srvId);
  const uni = pick(get_units_dd.data,        uniId);
  const src = pick(get_languages_dd.data,    srcId);
  const tgt = pick(get_languages_dd.data,    tgtId);

  const parts = [acc, srv, uni, (src && tgt) ? `${src} → ${tgt}` : ''].filter(Boolean);
  return parts.length ? parts.join(' · ') : (det.work_team_name || '—');
})()
}}
