onClick:
{{
(async () => {
  const ids = ms_add_resources.selectedOptionValues || [];
  if (!ids.length) { showAlert('Select at least one resource.', 'warning'); return; }
  if (!appsmith.store.wtRow?.work_team_id) { showAlert('No team context.', 'error'); return; }

  await ins_wtm_bulk.run();        // INSERT only
  await get_team_members.run();    // refresh the table
  resetWidget('ms_add_resources', true);
  if (get_team_members.data?.length) {
    await storeValue('wtmRow', get_team_members.data[0]); // keep detail tabs happy
  }
  showAlert('Resources added to team.', 'success');
})();
}}
