menuItem1.onClick:
{{
(() => {
  const rows = tbl_team_members.selectedRows || [];
  if (!rows.length) { showAlert('Select at least one member.', 'warning'); return; }

  // Build BCC list (alias â†’ fallback to resource email)
  const emails = rows
    .map(r => r.email_to || r.alias_email || r.resource_email)
    .filter(Boolean);

  if (!emails.length) { showAlert('No emails found in selection.', 'warning'); return; }

  // Resolve selected template (optional)
  const tpl = (get_email_templates_dd.data || [])
    .find(o => String(o.value) === String(i_email_template.selectedOptionValue));

  const subject = tpl?.template_email_subject || '';
  // Strip HTML tags from body to stay mailto-safe (Outlook mailto body is plain text)
  const rawBody = tpl?.template_email_body_html || '';
  const bodyTxt = String(rawBody).replace(/<[^>]+>/g, '');

  const url =
    'mailto:?bcc=' + encodeURIComponent(emails.join(','))
    + (subject ? '&subject=' + encodeURIComponent(subject) : '')
    + (bodyTxt ? '&body=' + encodeURIComponent(bodyTxt) : '');

  navigateTo(url, 'NEW_WINDOW');
})()
}}

menuItem2.onClick:
{{
(() => {
  const rows = tbl_team_members.selectedRows || [];
  const emails = rows
    .map(r => r.email_to || r.alias_email || r.resource_email)
    .filter(Boolean);

  if (!emails.length) { showAlert('No emails in selection.', 'warning'); return; }

  copyToClipboard(emails.join('; '));
  showAlert('Emails copied.', 'success');
})()
}}

menuItem3.onClick:
{{
(async () => {
  const rows = tbl_team_members.selectedRows || [];
  if (!rows.length) { showAlert('Select at least one member.', 'warning'); return; }

  try {
    await del_wtm_bulk_soft.run();
    await get_team_members.run();             // refresh with filters preserved
    showAlert('Selected members removed.', 'success');
  } catch (e) {
    showAlert('Delete failed: ' + (e?.message || e), 'error');
  }
})();
}}
